const validationEmail = (userData, errors, setErrors) => {
    const tempErrors = {};

    if (userData.email === '') {
      tempErrors.email = 'Campo Obligatorio';
    } else if (userData.email.length > 35) {
      tempErrors.email = 'No puede superar los 35 caracteres';
    } else if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(userData.email)) {
      tempErrors.email = 'Email invalido';
    } else {
        tempErrors.email = ''
    }

    setErrors({...errors, ...tempErrors});
  }

  export default validationEmail;
