const validation = (userData, errors, setErrors) => {

// ! VALIDACION EMAIL

    if(!userData.email) {
        setErrors({...errors, email: 'Campo Obligatorio'});
    } 
    else if(userData.email.lenght > 35) {
        setErrors({...errors, email: 'No puede superar los 35 caracteres'})
    } 
     else if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(userData.email)) {
        setErrors({...errors, email: ''})
    } else { 
        setErrors({...errors, email: 'Email invalido'})
    }

    // ! VALIDACION PASSWORD

    if(!userData.password) {
        setErrors({...errors, email: 'Debe haber una constraseña'});
    }
    else if(userData.password.length < 6){
        setErrors({...errors, email: 'Contraseña muy corta'});
    }
    else if(userData.password.length > 10) {
        setErrors({...errors, email: 'Maximo de caracteres alcanzado'});
    }
    else if(/[0-9]/.test(userData.password)) {
        setErrors({...errors, password: ''})
    }
    else {
        setErrors({...errors, password: 'La contraseña debe tener al menos 1 digito'})
    }
}

export default validation;

