const validationEmail = (userData, errors, setErrors) => {

    if(!userData.email)  setErrors({...errors, email: 'Campo Obligatorio'});
    else if(userData.email.lenght > 35) setErrors({...errors, email: 'No puede superar los 35 caracteres'})
    else if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(userData.email.value)) {
        setErrors({...errors, email: 'Email ivanlido'})
    } 
}

export default validationEmail;
