import ADD_FAVORITE from "./actions";

const initialState = {
    myFavorites: [],
}

const rootReducer = (state = initialState, action) => {
    switch(action.type){
        case ADD_FAVORITE:
            return {...state}

        default:
            return {...state};
    }
}

export default rootReducer;
